#include <stdio.h>
#include <stdlib.h>

int main(void){
	int i, n;
	char pi[] = "";
	char arg[10] = {};


	for (i = 0; i < 90; i++){
		sprintf(arg,"%c%c%c%c%c%c%c%c%c%c", pi[i+0], pi[i+1] , pi[i+2], pi[i+3] , pi[i+4], pi[i+5] , pi[i+6], pi[i+7], pi[i+8], pi[i+9]  );
		long int num = atol(arg);
		printf("i: %ld\n", num);
		for (n=2;n<num;n++){
			if (num%n==0)
			{
				printf("no prime!\n");
				break;
			}
		}

		if (num == n)
		{
			printf("prime!\n");
		}
	}
}